INSERT INTO `highlights` VALUES ('164d0784-37e9-4142-b1ea-27f4e0504aa1','[{\"pageContent\":\"Atlas\",\"metadata\":{\"chunkId\":\"ea7cafec-330f-4591-a68b-724d457f8931\",\"innerChunkNo\":11,\"pageNums\":[2]}},{\"pageContent\":\"Atlas\",\"metadata\":{\"chunkId\":\"6a05f6b9-8a73-4b7b-b189-b79e3cb25b65\",\"innerChunkNo\":11,\"pageNums\":[2]},\"highlight\":[{\"rect_info\":{\"x1\":306.308816361,\"x2\":469.625292035,\"y1\":109.0829658097419,\"y2\":164.22243705361,\"width\":0,\"height\":0},\"content\":\"Atlas\",\"chunk_id\":\"6a05f6b9-8a73-4b7b-b189-b79e3cb25b65\",\"origin_info\":{\"id\":\"2f0f3fbb-0266-4bad-b48c-317ef9c27c6a\",\"dir\":\"ltr\",\"str\":\"Atlas\",\"rect\":{\"x1\":306.308816361,\"x2\":469.625292035,\"y1\":109.0829658097419,\"y2\":164.22243705361,\"width\":0,\"height\":0},\"width\":44.65803629129667,\"hasEOL\":true,\"height\":14.889982759168,\"chunk_id\":\"6a05f6b9-8a73-4b7b-b189-b79e3cb25b65\",\"fontName\":\"g_d0_f1\",\"attribute\":{\"source\":\"Izacard 等 - 2022 - Atlas Few-shot Learning with Retrieval Augmented .pdf\",\"indexName\":\"Index_0733a2e59ea76b3c031f85dc9ca660df\",\"innerChunkNo\":0},\"transform\":[],\"pageNumber\":2},\"pageNumber\":2}]},{\"pageContent\":\"In this paper, we introduce Atlas, a large retrieval-augmented language model. By jointly pre-training the retriever module and the language model, we show that Atlas has strong few-shot learning capabilities on a wide range of knowledge intensive tasks, including NaturalQuestions, TriviaQA, FEVER, 8 KILT tasks and 57 MMLU tasks. For example, Atlas-11B reaches more than 42% accuracy on NaturalQuestions and 84.7% on TriviaQA when training on 64 examples, which is an improvement of almost 3 points compared to PaLM, a 540B parameters model, which required 50x more pre-training compute. We also provided detailed ablations and analyses for what factors are important when training such retrieval-augmented models, and demonstrated Atlas’s updateability, interpretability and controlability capabilities. Lastly, we demonstrated that Atlas is also powerful in full-dataset settings obtaining a new state-of-the-art results on NaturalQuestions, TriviaQA, FEVER, and 5 KILT tasks.\",\"metadata\":{\"chunkId\":\"52f56166-4cd3-435b-a719-4e5d2b60973c\",\"innerChunkNo\":85,\"pageNums\":[19]}},{\"pageContent\":\"In this paper, we introduce Atlas, a large retrieval-augmented language model. By jointly pre-training the retriever module and the language model, we show that Atlas has strong few-shot learning capabilities on a wide range of knowledge intensive tasks, including NaturalQuestions, TriviaQA, FEVER, 8 KILT tasks and 57 MMLU tasks. For example, Atlas-11B reaches more than 42% accuracy on NaturalQuestions and 84.7% on TriviaQA when training on 64 examples, which is an improvement of almost 3 points compared to PaLM, a 540B parameters model, which required 50x more pre-training compute. We also provided detailed ablations and analyses for what factors are important when training such retrieval-augmented models, and demonstrated Atlas’s updateability, interpretability and controlability capabilities. Lastly, we demonstrated that Atlas is also powerful in full-dataset settings obtaining a new state-of-the-art results on NaturalQuestions, TriviaQA, FEVER, and 5 KILT tasks.\",\"metadata\":{\"chunkId\":\"71ceb46e-8312-4277-b06d-d41fc32956a2\",\"innerChunkNo\":85,\"pageNums\":[19]},\"highlight\":[{\"rect_info\":{\"x1\":72,\"x2\":539.999461251,\"y1\":522.2944000000001,\"y2\":532.2570000000001,\"width\":612,\"height\":792},\"content\":\"540B parameters model, which required 50x more pre-training compute. We also provided detailed ablations\",\"chunk_id\":\"71ceb46e-8312-4277-b06d-d41fc32956a2\",\"origin_info\":{\"id\":\"0b1eff17-975e-46ca-ac7d-9a4ee08481f0\",\"dir\":\"ltr\",\"str\":\"540B parameters model, which required 50x more pre-training compute. We also provided detailed ablations\",\"rect\":{\"x1\":72,\"x2\":539.999461251,\"y1\":522.2944000000001,\"y2\":532.2570000000001,\"wid